CREATE OR REPLACE FUNCTION obter_dados_solicitacao_medica (nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_material_w	varchar(255);
ds_fornecedor_w	varchar(255);


BEGIN

select	max(ds_material),
	max(ds_fornecedor)
into STRICT	ds_material_w,
	ds_fornecedor_w
from	agenda_pac_consignado
where	nr_sequencia = nr_sequencia_p;

if (ie_opcao_p = 'M') then
	return	ds_material_w;
elsif (ie_opcao_p = 'F') then
	return	ds_fornecedor_w;
else
	return null;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
