CREATE OR REPLACE FUNCTION obter_dados_solicitacao ( nr_Seq_solicitacao_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/* 
DS - Nome do Solicitante 
*/
 
 
cd_solicitante_w varchar(10);
ds_retorno_w   varchar(255);


BEGIN 
 
select	cd_pf_solic 
into STRICT	cd_solicitante_w 
from	san_solicitacao 
where	nr_sequencia	= nr_seq_solicitacao_p;
 
if (ie_opcao_p	= 'DS')	then 
	ds_retorno_w	:= substr(obter_nome_pf(cd_solicitante_w),1,255);
	 
end if;
 
Return ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
