SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_solic_compra ( nr_solic_compra_p bigint, ie_tipo_dado_p text) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w			varchar(100)	:= '';
cd_pessoa_solicitante_w		varchar(20);
ds_pessoa_solicitante_w		varchar(60);
dt_baixa_w			timestamp;
ds_setor_responsavel_w		varchar(100);
cd_setor_responsavel_w		integer;
cd_comprador_resp_w		varchar(10);
ie_aviso_aprov_oc_w		varchar(1);
cd_centro_custo_w			integer;
cd_setor_atendimento_w		integer;
cd_local_estoque_w		smallint;
dt_pre_aprovacao_w		timestamp;
dt_liberacao_w			timestamp;

/*ie_tipo_dado 
0 - pessoa solicitante 
1 - nome pessoa solicitante 
2 - data baixa 
3 - Nome do setor responsavel (é o setor do comprador responsavel ou do solicitante) 
4 - Código do setor responsavel (é o setor do comprador responsavel ou do solicitante) 
5 - Código comprador responsavel 
6 - Avisa aprovação OC 
7 - código do centro de custo 
8 - Codigo setor atendimento 
9 - código local estoque 
10 - dt_pre_aprovacao_w 
11 - Descrição do centro de custo 
12 - dt_liberacao 
13 - título a pagar 
*/
 
 

BEGIN 
 
if (coalesce(nr_solic_compra_p,0) > 0) then 
	select	cd_pessoa_solicitante, 
		substr(obter_nome_pessoa_fisica(cd_pessoa_solicitante, null),1,60), 
		dt_baixa, 
		substr(obter_nome_setor(obter_setor_usuario(obter_usuario_pessoa(coalesce(cd_comprador_resp, cd_pessoa_solicitante)))),1,100), 
		obter_setor_usuario(obter_usuario_pessoa(coalesce(cd_comprador_resp, cd_pessoa_solicitante))), 
		cd_comprador_resp, 
		coalesce(ie_aviso_aprov_oc,'N'), 
		cd_centro_custo, 
		cd_setor_atendimento, 
		cd_local_estoque, 
		dt_pre_aprovacao, 
		dt_liberacao 
	into STRICT	cd_pessoa_solicitante_w, 
		ds_pessoa_solicitante_w, 
		dt_baixa_w, 
		ds_setor_responsavel_w, 
		cd_setor_responsavel_w, 
		cd_comprador_resp_w, 
		ie_aviso_aprov_oc_w, 
		cd_centro_custo_w, 
		cd_setor_atendimento_w, 
		cd_local_estoque_w, 
		dt_pre_aprovacao_w, 
		dt_liberacao_w 
	from	solic_compra 
	where	nr_solic_compra	= nr_solic_compra_p;
 
	if (ie_tipo_dado_p = 0) then 
		ds_retorno_w	:= cd_pessoa_solicitante_w;
	elsif (ie_tipo_dado_p = 1) then 
		ds_retorno_w	:= ds_pessoa_solicitante_w;
	elsif (ie_tipo_dado_p = 2) then 
		ds_retorno_w	:= dt_baixa_w;
	elsif (ie_tipo_dado_p = 3) then 
		ds_retorno_w	:= ds_setor_responsavel_w;
	elsif (ie_tipo_dado_p = 4) then 
		ds_retorno_w	:= cd_setor_responsavel_w;
	elsif (ie_tipo_dado_p = 5) then 
		ds_retorno_w	:= cd_comprador_resp_w;
	elsif (ie_tipo_dado_p = 6) then 
		ds_retorno_w	:= ie_aviso_aprov_oc_w;
	elsif (ie_tipo_dado_p = 7) then 
		ds_retorno_w	:= cd_centro_custo_w;
	elsif (ie_tipo_dado_p = 8) then 
		ds_retorno_w	:= cd_setor_atendimento_w;
	elsif (ie_tipo_dado_p = 9) then 
		ds_retorno_w	:= cd_local_estoque_w;
	elsif (ie_tipo_dado_p = 10) then 
		ds_retorno_w	:= dt_pre_aprovacao_w;
	elsif (ie_tipo_dado_p = 11) then 
		select	substr(obter_desc_centro_custo(cd_centro_custo_w),1,100) 
		into STRICT	ds_retorno_w 
		;
	elsif (ie_tipo_dado_p = 12) then 
		ds_retorno_w	:= dt_liberacao_w;
	elsif (ie_tipo_dado_p = 13) then 
		select	max(nr_titulo) 
		into STRICT	ds_retorno_w 
		from	titulo_pagar 
		where	nr_solic_compra = nr_solic_compra_p;
	end if;
end if;
 
return ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_dados_solic_compra ( nr_solic_compra_p bigint, ie_tipo_dado_p text) FROM PUBLIC;

