SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_sol_dialise ( nr_prescricao_p bigint, nr_seq_solucao_p bigint, ie_tipo_dialise_p text, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


qt_retorno_w	double precision;
qt_sol_total_w	double precision;
qt_vol_inf_w	double precision;

/*
E   - Etapas
VI - Volume infundido
VR - Volume restante
*/
BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_solucao_p IS NOT NULL AND nr_seq_solucao_p::text <> '') then

	if (ie_tipo_dialise_p = 'P') then
		if (ie_opcao_p = 'VI') then

			select	sum(qt_volume)
			into STRICT	qt_retorno_w
			from	hd_prescricao_evento
			where	nr_prescricao  = nr_prescricao_p
			and	nr_seq_solucao = nr_seq_solucao_p
			and	ie_evento = 'IT';

		elsif (ie_opcao_p = 'VR') then

			select	coalesce(max(qt_solucao_total),0)
			into STRICT	qt_sol_total_w
			from	prescr_solucao
			where	nr_prescricao  = nr_prescricao_p
			and	nr_seq_solucao = nr_seq_solucao_p;

			select	sum(qt_volume)
			into STRICT	qt_vol_inf_w
			from	hd_prescricao_evento
			where	nr_prescricao  = nr_prescricao_p
			and	nr_seq_solucao = nr_seq_solucao_p
			and	ie_evento = 'IT';

			qt_retorno_w := (qt_sol_total_w - qt_vol_inf_w);

		elsif (ie_opcao_p = 'E') then

			select	max(nr_etapa_evento)
			into STRICT	qt_retorno_w
			from	hd_prescricao_evento
			where	nr_prescricao  = nr_prescricao_p
			and	nr_seq_solucao = nr_seq_solucao_p;

		end if;
	end if;

end if;

return	round((qt_retorno_w)::numeric,2);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
