CREATE OR REPLACE FUNCTION obter_dados_strong_kids ( nr_sequencia_p bigint, dt_entrada_p timestamp, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE

/*
Descrição												Opção
Quantidade de avaliações realizadas até 24h após a data de entrada de internação	24
Quantidade de avaliações realizadas até 48h após a data de entrada de internação	48
Quantidade de avaliações realizadas até 72h após a data de entrada de internação	72
*/
qt_retorno_w		bigint;
nr_atendimento_w	bigint;
dt_liberacao_w		timestamp;


BEGIN

if ( ie_opcao_p = '24'  ) then

	Select 	count(*)
	into STRICT	qt_retorno_w
	from	escala_strong_kids
	where	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
	and 	coalesce(dt_inativacao::text, '') = ''
	and		nr_sequencia = nr_sequencia_p
	and		dt_liberacao > dt_entrada_p
	and		(dt_liberacao <= (dt_entrada_p + 1));

elsif (ie_opcao_p = '48') then

	Select 	count(*)
	into STRICT	qt_retorno_w
	from	escala_strong_kids
	where	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
	and 	coalesce(dt_inativacao::text, '') = ''
	and		nr_sequencia = nr_sequencia_p
	and		dt_liberacao > dt_entrada_p
	and		(dt_liberacao <= (dt_entrada_p + 2));


elsif (ie_opcao_p = '72') then

	Select 	count(*)
	into STRICT	qt_retorno_w
	from	escala_strong_kids
	where	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
	and 	coalesce(dt_inativacao::text, '') = ''
	and		nr_sequencia = nr_sequencia_p
	and		dt_liberacao > dt_entrada_p
	and		(dt_liberacao <= (dt_entrada_p + 3));

end if;

return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
