SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_sumario_res ( nr_seq_sumario_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
Descrição				Opção
Nome do tipo de sumário		N
Data de atualização		D
*/
ds_date_w				varchar(255);
ds_retorno_w			varchar(255);
ds_sumario_config_w		varchar(255);
ie_tipo_sumario_w		varchar(15);


BEGIN

if (nr_seq_sumario_p > 0 ) then

	Select 	max(obter_valor_dominio(8894,ie_tipo_sumario)),
			max(pkg_date_formaters.to_varchar(dt_atualizacao, 'short', wheb_usuario_pck.get_cd_estabelecimento, wheb_usuario_pck.get_nm_usuario )),
			max(ds_sumario_config),
			max(ie_tipo_sumario)
	into STRICT	ds_retorno_w,
			ds_date_w,
			ds_sumario_config_w,
			ie_tipo_sumario_w
	from	imp_sumario_paciente
	where	nr_sequencia = nr_seq_sumario_p;

	if ( ie_tipo_sumario_w = 'CONF') then

			ds_retorno_w :=  ds_sumario_config_w||' ('||obter_desc_expressao(285689)||') ';

	end if;

	if ( ie_opcao_p = 'D' ) then

		ds_retorno_w := ds_date_w;

	end if;


end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
