SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_tabela_evolucao ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*

UM - Unidade Medida
UT - Unidade Tempo

*/
ds_unidade_medida_w	varchar(30);
ds_unidade_tempo_w	varchar(30);
ds_atributo_pre_w	varchar(255);


BEGIN

select	CASE WHEN upper(ie_unidade_medida)=upper(obter_unid_med_usua('KG')) THEN  substr(obter_desc_expressao(295717), 1, 255) WHEN upper(ie_unidade_medida)=upper(obter_unid_med_usua('G')) THEN  substr(obter_desc_expressao(295716), 1, 255) WHEN upper(ie_unidade_medida)=upper(obter_unid_med_usua('CM')) THEN  substr(obter_desc_expressao(726975), 1,255) END ,
		CASE WHEN ie_unidade_tempo='DIAS' THEN substr(obter_desc_expressao(287753),1,255) WHEN ie_unidade_tempo='S' THEN  substr(obter_desc_expressao(310380), 1,255) WHEN ie_unidade_tempo='M' THEN  substr(obter_desc_expressao(293215), 1,255) WHEN ie_unidade_tempo='A' THEN  substr(obter_desc_expressao(283493), 1,255) WHEN ie_unidade_tempo='SEM' THEN  substr(obter_desc_expressao(310380), 1,255)  END ,
	obter_valor_dominio(3936,IE_ATRIBUTO_PRE)
into STRICT	ds_unidade_medida_w,
	ds_unidade_tempo_w,
	ds_atributo_pre_w
from	tabela_evolucao
where	nr_sequencia	= nr_sequencia_p;

if (ie_opcao_p = 'UM') then
	if (coalesce(ds_atributo_pre_w::text, '') = '') then
		return	ds_unidade_medida_w;
	else
		return	ds_atributo_pre_w;
	end if;
elsif (ie_opcao_p = 'UT') then
	return	ds_unidade_tempo_w;
end	if;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
