SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_tasy_help ( nr_seq_help_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
ie_opcao_p
1 - Tipo objeto
2 - Responsável tipo objeto
3 - Função
*/
ie_tipo_objeto_w	varchar(01);
ie_responsavel_w	varchar(15);
cd_funcao_w		integer;
nr_seq_mod_impl_w	bigint;


BEGIN

select	max(a.ie_tipo_objeto),
	max(a.cd_funcao),
	max(a.nr_seq_mod_impl)
into STRICT	ie_tipo_objeto_w,
	cd_funcao_w,
	nr_seq_mod_impl_w
from	tasy_objeto_help a,
	tasy_help b
where	a.nr_sequencia = b.nr_seq_objeto
and	b.nr_sequencia = nr_seq_help_p;

select	max(ie_responsavel)
into STRICT	ie_responsavel_w
from	tasy_tipo_help
where	ie_tipo_objeto = ie_tipo_objeto_w;

if (ie_opcao_p	= '1') then
	return	ie_tipo_objeto_w;
elsif (ie_opcao_p	= '2') then
	return	ie_responsavel_w;
elsif (ie_opcao_p	= '3') then
	return	cd_funcao_w;
elsif (ie_opcao_p = '4') then
	return	nr_seq_mod_impl_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_dados_tasy_help ( nr_seq_help_p bigint, ie_opcao_p text) FROM PUBLIC;

