CREATE OR REPLACE FUNCTION obter_dados_tcp ( nr_sequencia_p bigint, ie_opcao_p text, ie_html5_p text default null) RETURNS varchar AS $body$
DECLARE


ds_cor_w	varchar(15);
nr_seq_prioridade_w	bigint;
ds_classificacao_w	varchar(500);

BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	select	case when coalesce(ie_html5_p::text, '') = '' then ds_cor else ds_cor_html end,	
		nr_seq_prioridade,
		ds_classificacao
	into STRICT	ds_cor_w,
		nr_seq_prioridade_w,
		ds_classificacao_w
	from	triagem_classif_prioridade
	where	nr_sequencia	= nr_sequencia_p;
	
	if (ie_opcao_p	= 'SEQ') then
		return coalesce(nr_seq_prioridade_w,999);
	elsif (ie_opcao_p	= 'DESC') then
		return ds_classificacao_w;
	elsif (ie_opcao_p	= 'COR') then
		return ds_cor_w;
	end if;

end if;

return null;

END;
$body$
