CREATE OR REPLACE FUNCTION obter_dados_terceiro_operacao ( nr_seq_terceiro_operacao_p bigint, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN

if (ie_tipo_p = 'DT') then

	select	to_char(max(dt_operacao),'dd/mm/yyyy')
	into STRICT	ds_retorno_w
	from	terceiro_operacao
	where	nr_sequencia = nr_seq_terceiro_operacao_p;

elsif (ie_tipo_p = 'DC') then

	select	max(nr_doc)
	into STRICT	ds_retorno_w
	from	terceiro_operacao
	where	nr_sequencia = nr_seq_terceiro_operacao_p;

end if;


return	ds_retorno_w;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
