CREATE OR REPLACE FUNCTION obter_dados_terceiro_relatorio (nr_seq_terceiro_p bigint, cd_relatorio_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*ie_opcao_p
M	Médico
CC	Conta Contábil
CCP	Conta Contábil Provisão
CF	Conta Financeira
S	Serviço
NG	Nome Gestor
CG 	Cargo Gestor
TF	Transação Financeira
*/
ds_medicos_w			varchar(4000);
cd_conta_contabil_w		varchar(20);
cd_conta_contabil_prov_w	varchar(20);
ds_servico_w			varchar(255);
nm_gestor_w			varchar(255);
ds_cargo_gestor_w		varchar(255);
ds_retorno_w			varchar(4000);
cd_conta_financ_w		bigint;
nr_seq_trans_fin_tit_w		bigint;


BEGIN

select	max(ds_medicos),
	max(cd_conta_contabil),
	max(ds_servico),
	max(nm_gestor),
	max(ds_cargo_gestor)
into STRICT	ds_medicos_w,
	cd_conta_contabil_w,
	ds_servico_w,
	nm_gestor_w,
	ds_cargo_gestor_w
from	terceiro_relatorio
where	nr_seq_terceiro		= nr_seq_terceiro_p
and	cd_relatorio		= cd_relatorio_p;

select	max(cd_conta_contabil),
	max(cd_conta_financ),
	max(nr_seq_trans_fin_tit)
into STRICT	cd_conta_contabil_prov_w,
	cd_conta_financ_w,
	nr_seq_trans_fin_tit_w
from	terceiro
where	nr_sequencia	= nr_seq_terceiro_p;

if (ie_opcao_p = 'M') then
	ds_retorno_w	:= ds_medicos_w;
elsif (ie_opcao_p = 'CC') then
	ds_retorno_w	:= cd_conta_contabil_w;
elsif (ie_opcao_p = 'CCP') then
	ds_retorno_w	:= cd_conta_contabil_prov_w;
elsif (ie_opcao_p = 'CF') then
	ds_retorno_w	:= cd_conta_financ_w;
elsif (ie_opcao_p = 'S') then
	ds_retorno_w	:= ds_servico_w;
elsif (ie_opcao_p = 'NG') then
	ds_retorno_w	:= nm_gestor_w;
elsif (ie_opcao_p = 'CG') then
	ds_retorno_w	:= ds_cargo_gestor_w;
elsif (ie_opcao_p = 'TF') then
	ds_retorno_w	:= substr(OBTER_DESC_TRANS_FINANC(nr_seq_trans_fin_tit_w),1,255);
end if;

return 	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
