CREATE OR REPLACE FUNCTION obter_dados_tipo_evolucao ( nr_atendimento_p bigint, ie_tipo_evolucao_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
nm_profissional_w	varchar(100);
ds_retorno_w		varchar(255);
dt_evolucao_w		timestamp;


BEGIN 
if (ie_opcao_p in ('DPA','PPA')) then 
	select	max(obter_nome_pf(cd_medico)), 
		max(dt_evolucao) 
	into STRICT	nm_profissional_w, 
		dt_evolucao_w 
	from	evolucao_paciente 
	where	cd_evolucao	=	(	SELECT	min(cd_evolucao) 
						from	evolucao_paciente 
						where	nr_atendimento	=	nr_atendimento_p 
						and	ie_situacao	=	'A' 
						and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '') 
						and	obter_se_contido(campo_numerico(ie_tipo_evolucao),ie_tipo_evolucao_p) = 'S');
						 
	if (ie_opcao_p = 'DPA') then 
		ds_retorno_w	:= to_char(dt_evolucao_w,'dd/mm/yyyy hh24:mi:ss');
	elsif (ie_opcao_p = 'PPA') then 
		ds_retorno_w	:= nm_profissional_w;
	end if;
elsif (ie_opcao_p in ('DUA','PUA')) then 
	select	max(obter_nome_pf(cd_medico)), 
		max(dt_evolucao) 
	into STRICT	nm_profissional_w, 
		dt_evolucao_w 
	from	evolucao_paciente 
	where	cd_evolucao	=	(	SELECT	max(cd_evolucao) 
						from	evolucao_paciente 
						where	nr_atendimento	=	nr_atendimento_p 
						and	ie_situacao	=	'A' 
						and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '') 
						and	obter_se_contido(campo_numerico(ie_tipo_evolucao),ie_tipo_evolucao_p) = 'S');
						 
	if (ie_opcao_p = 'DUA') then 
		ds_retorno_w	:= to_char(dt_evolucao_w,'dd/mm/yyyy hh24:mi:ss');
	elsif (ie_opcao_p = 'PUA') then 
		ds_retorno_w	:= nm_profissional_w;
	end if;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
