CREATE OR REPLACE FUNCTION obter_dados_tipo_isolamento ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_tipo_isolamento_w	varchar(80);
ds_retorno_w		varchar(255);
ds_cor_w		varchar(15);


BEGIN

select	ds_tipo_isolamento,
	ds_cor
into STRICT	ds_tipo_isolamento_w,
	ds_cor_w
from	cih_tipo_isolamento
where	nr_sequencia	=	nr_sequencia_p;

if (ie_opcao_p = 'D') then
	ds_retorno_w	:= ds_tipo_isolamento_w;
elsif (ie_opcao_p = 'C') then
	ds_retorno_w	:= ds_cor_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
