SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_tipo_ocor ( nr_seq_tipo_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_observacao_w				varchar(255) := '';
ds_descricao_w				varchar(255)   := '';
ds_tipo_ocorrencia_w			varchar(50) := '';

/* ie_opcao_p
	O - Observacao
*/
BEGIN
select	max(ds_observacao),
	max(ds_tipo_ocorrencia)
into STRICT	ds_observacao_w,
	ds_tipo_ocorrencia_w
from	sac_tipo_ocorrencia
where	nr_sequencia = nr_seq_tipo_p;

if (ie_opcao_p = 'O') then
	ds_descricao_w := ds_observacao_w;
elsif (ie_opcao_p = 'D') then
	ds_descricao_w := ds_tipo_ocorrencia_w;
else
	ds_descricao_w	:= '';
end if;

RETURN ds_descricao_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
