SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_titulo_repasse (nr_repasse_terceiro_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
T - Titulo
D - data de vencimento
I - impostos
*/
ds_lista_titulo_w	varchar(254) := '';
nr_titulo_w		bigint;
dt_vencimento_w		timestamp;
vl_imposto_w		double precision;

c01 CURSOR FOR
SELECT	nr_titulo,
	dt_vencimento_original
from	titulo_pagar
where	nr_repasse_terceiro	= nr_repasse_terceiro_p

union

select	nr_titulo,
	dt_vencimento
from	repasse_terceiro_venc
where	nr_repasse_terceiro	= nr_repasse_terceiro_p
order	by nr_titulo;


BEGIN

open c01;
loop
fetch c01 into
	nr_titulo_w,
	dt_vencimento_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	if (coalesce(ds_lista_titulo_w::text, '') = '') then
		if (ie_opcao_p = 'D') then
			ds_lista_titulo_w	:= to_char(dt_vencimento_w,'dd/mm/yyyy');
		else
			ds_lista_titulo_w	:= nr_titulo_w;
		end if;
	else
		if (ie_opcao_p = 'D') then
			ds_lista_titulo_w	:= ds_lista_titulo_w || ', ' || to_char(dt_vencimento_w,'dd/mm/yyyy');
		else
			ds_lista_titulo_w	:= ds_lista_titulo_w || ', ' || nr_titulo_w;
		end if;
	end if;

end loop;
close c01;

if (ie_opcao_p = 'I') then

	select	sum(vl_imposto)
	into STRICT	vl_imposto_w
	from (SELECT	a.vl_imposto
	from	repasse_terc_venc_trib a,
		repasse_terceiro_venc b
	where	b.nr_repasse_terceiro	= nr_repasse_terceiro_p
	and	a.nr_seq_rep_venc		= b.nr_sequencia
	
union	all

	PERFORM	a.vl_imposto
	from	titulo_pagar_imposto a,
		titulo_pagar b
	where	b.nr_repasse_terceiro	= nr_repasse_terceiro_p
	and	b.nr_titulo		= a.nr_titulo
	and	not exists (select	1
		from	repasse_terc_venc_trib y,
			repasse_terceiro_venc x
		where	x.nr_repasse_terceiro	= b.nr_repasse_terceiro
		and	x.nr_sequencia		= y.nr_seq_rep_venc)
	) alias3;

	ds_lista_titulo_w	:= vl_imposto_w;

end if;

return ds_lista_titulo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_dados_titulo_repasse (nr_repasse_terceiro_p bigint, ie_opcao_p text) FROM PUBLIC;

