SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_tit_rec_liq ( nr_titulo_p bigint, nr_seq_liq_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
dtr - data de depósito no retorno convênio
crn - crédito não identificado vinculado
sqp - sequência da perda
*/
ds_retorno_w		varchar(255);
nr_seq_retorno_w		double precision;
dt_deposito_w		timestamp;
nr_seq_movto_pend_w	bigint;
nr_seq_perda_w		bigint;
nr_seq_liq_origem_w	integer;
nr_seq_ret_estorno_w	bigint;
nr_interno_conta_w		bigint;
cd_autorizacao_w		varchar(20);
nr_seq_ret_item_w		bigint;


BEGIN
if (ie_opcao_p	= 'DTR') then
	begin
	select	max(nr_seq_retorno)
	into STRICT	nr_seq_retorno_w
	from	titulo_receber_liq
	where	nr_titulo	= nr_titulo_p
	and	nr_sequencia	= nr_seq_liq_p;

	select	max(c.dt_recebimento)
	into STRICT	dt_deposito_w
	from	convenio_receb c,
		convenio_ret_receb b
	where	b.nr_seq_receb		= c.nr_sequencia
	and	b.nr_seq_retorno	= nr_seq_retorno_w;
	ds_retorno_w	:= dt_deposito_w;
	end;
elsif (ie_opcao_p	= 'CRN') then
	select	max(a.nr_seq_movto_pend)
	into STRICT	nr_seq_movto_pend_w
	from	movto_banco_pend_baixa a
	where	a.nr_titulo	= nr_titulo_p
	and	a.nr_seq_baixa	= nr_seq_liq_p;

	ds_retorno_w	:= nr_seq_movto_pend_w;
elsif (ie_opcao_p	= 'SQP') then
	select	max(nr_seq_liq_origem),
		max(nr_seq_ret_item)
	into STRICT	nr_seq_liq_origem_w,
		nr_seq_ret_item_w
	from	titulo_receber_liq
	where	nr_titulo = nr_titulo_p
	and	nr_sequencia = nr_Seq_liq_p;

	select	max(nr_sequencia)
	into STRICT	nr_seq_perda_w
	from	perda_contas_receber
	where	nr_titulo = nr_titulo_p
	and	nr_seq_baixa = coalesce(nr_seq_liq_origem_w, nr_seq_liq_p);

	if (coalesce(nr_seq_perda_w::text, '') = '') and (coalesce(nr_seq_liq_origem_w::text, '') = '') and (nr_seq_ret_item_w IS NOT NULL AND nr_seq_ret_item_w::text <> '') then
		begin
		select	max(c.nr_seq_ret_estorno),
			max(b.nr_interno_conta),
			max(b.cd_autorizacao)
		into STRICT	nr_seq_ret_estorno_w,
			nr_interno_conta_w,
			cd_autorizacao_w
		from	titulo_receber_liq a,
			convenio_retorno_item b,
			convenio_retorno c
		where	a.nr_seq_ret_item	= b.nr_sequencia
		and	b.nr_seq_retorno	= c.nr_sequencia
		and	a.nr_titulo		= nr_titulo_p
		and	a.nr_sequencia		= nr_seq_liq_p;

		select	max(a.nr_sequencia)
		into STRICT	nr_seq_liq_origem_w
		from	titulo_receber_liq a,
			convenio_retorno_item b,
			convenio_retorno c
		where	a.nr_seq_ret_item	= b.nr_sequencia
		and	b.nr_seq_retorno	= c.nr_sequencia
		and	a.nr_titulo		= nr_titulo_p
		and	c.nr_sequencia		= nr_seq_ret_estorno_w
		and	b.nr_interno_conta	= nr_interno_conta_w
		and	b.cd_autorizacao	= cd_autorizacao_w;

		select	max(nr_sequencia)
		into STRICT	nr_seq_perda_w
		from	perda_contas_receber
		where	nr_titulo = nr_titulo_p
		and	nr_seq_baixa = nr_seq_liq_origem_w;
		end;
	end if;

	ds_retorno_w := nr_seq_perda_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
