CREATE OR REPLACE FUNCTION obter_dados_tof_meta ( nr_seq_meta_p bigint, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE

/*ie_tipo
D = ds_meta
T = ie_tipo_meta
R = ie_regra
*/
ds_retorno_w	varchar(255);
ds_meta_w	varchar(255);
ie_tipo_meta_w	varchar(3);
ie_regra_w	varchar(10);



BEGIN
select	ds_meta,
	ie_tipo_meta,
	ie_regra
into STRICT	ds_meta_w,
	ie_tipo_meta_w,
	ie_regra_w
from	tof_meta
where	nr_sequencia = nr_seq_meta_p;

if (ie_tipo_p = 'D') then
ds_retorno_w := ds_meta_w;
elsif (ie_tipo_p = 'T') then
ds_retorno_w := ie_tipo_meta_w;
elsif (ie_tipo_p = 'R') then
ds_retorno_w := ie_regra_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
