CREATE OR REPLACE FUNCTION obter_dados_transcricao (nr_seq_transcricao_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* ie_opcao_p:
'CL' = Classificacao
*/
ds_retorno_w	varchar(100);


BEGIN
if (nr_seq_transcricao_p IS NOT NULL AND nr_seq_transcricao_p::text <> '') then
	if (ie_opcao_p = 'CL') then
		select	max(nr_seq_classificacao)
		into STRICT	ds_retorno_w
		from	transcricao_prescricao
		where	nr_sequencia = nr_seq_transcricao_p;
	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
