SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_transf_mx (nr_titulo_p bigint, nr_seq_baixa_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w   		varchar(255);
cd_pessoa_fisica_w      pessoa_fisica.cd_pessoa_fisica%type;
cd_cgc_w                pessoa_juridica.cd_cgc%type;
nr_seq_pais_w           pais.nr_sequencia%type;
ds_banco_w              banco.ds_banco%type;
cd_cnpj_agencia_w 	pessoa_juridica.cd_cgc%type;
sg_pais_w               pais.sg_pais%type;


BEGIN

select  max(a.cd_cgc),
	max(a.cd_pessoa_fisica)
into STRICT	cd_cgc_w,
	cd_pessoa_fisica_w
from    titulo_receber a,
	titulo_receber_liq b
where   a.nr_titulo = b.nr_titulo
and     a.nr_titulo = nr_titulo_p
and     exists (SELECT 1
		from  movto_trans_financ c
		where b.nr_seq_movto_trans_fin = c.nr_sequencia);


	if (ie_opcao_p = 'RFC') then   -- rfc da banco vinculado a pessoa do titulo
		if (cd_cgc_w IS NOT NULL AND cd_cgc_w::text <> '') then

			select	max(a.cd_cnpj_agencia)
			into STRICT	cd_cnpj_agencia_w
			from	pessoa_juridica_conta a
			where	a.cd_cgc = cd_cgc_w;

			if (coalesce(cd_cnpj_agencia_w,'X') <> 'X')then

				select	max(cd_rfc)
				into STRICT	ds_retorno_w
				from 	pessoa_juridica
				where	cd_cgc = cd_cnpj_agencia_w;

			end if;

		elsif (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then

			select	max(a.cd_cnpj_agencia)
			into STRICT	cd_cnpj_agencia_w
			from	pessoa_fisica_conta a
			where	a.cd_pessoa_fisica = cd_pessoa_fisica_w;

			if (coalesce(cd_cnpj_agencia_w,'X') <> 'X')then

				select	max(cd_rfc)
				into STRICT	ds_retorno_w
				from 	pessoa_juridica
				where	cd_cgc = cd_cnpj_agencia_w;

			end if;

		end if;

	elsif (ie_opcao_p = 'B') then -- nome do banco
		if (cd_cgc_w IS NOT NULL AND cd_cgc_w::text <> '') then

			select	max(a.cd_cnpj_agencia),
				max(obter_nome_banco(a.cd_banco))
			into STRICT	cd_cnpj_agencia_w,
				ds_banco_w
			from	pessoa_juridica_conta a
			where	a.cd_cgc = cd_cgc_w;

			if (coalesce(cd_cnpj_agencia_w,'X') <> 'X') then

				select  max(nr_seq_pais)
				into STRICT    nr_seq_pais_w
				from 	pessoa_juridica
				where   cd_cgc = cd_cnpj_agencia_w;

				select 	coalesce(max(obter_dados_pais(nr_seq_pais_w,'SG')),'MEX')
				into STRICT   	sg_pais_w
				;

				if (sg_pais_w <> 'MEX') then

					ds_retorno_w := ds_banco_w;

				end if;

			end if;

		elsif (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then

			select	max(a.cd_cnpj_agencia),
				max(obter_nome_banco(a.cd_banco))
			into STRICT	cd_cnpj_agencia_w,
				ds_banco_w
			from	pessoa_fisica_conta a
			where	a.cd_pessoa_fisica = cd_pessoa_fisica_w;

			if (coalesce(cd_cnpj_agencia_w,'X') <> 'X') then

				select  max(nr_seq_pais)
				into STRICT    nr_seq_pais_w
				from 	pessoa_juridica
				where   cd_cgc = cd_cnpj_agencia_w;

				select 	coalesce(max(obter_dados_pais(nr_seq_pais_w,'SG')),'MEX')
				into STRICT   	sg_pais_w
				;

				if (sg_pais_w <> 'MEX') then

					ds_retorno_w := ds_banco_w;

				end if;

			end if;

		end if;

	elsif (ie_opcao_p = 'C') then	 -- conta do banco da pessoa do t√≠tulo a receber
		if (cd_cgc_w IS NOT NULL AND cd_cgc_w::text <> '') then

			select	max(b.nr_conta)
			into STRICT	ds_retorno_w
			from	pessoa_juridica a,
				pessoa_juridica_conta b,
				titulo_receber c,
				titulo_receber_liq d
			where   a.cd_cgc = b.cd_cgc
			and     a.cd_cgc = c.cd_cgc
			and     c.nr_titulo = d.nr_titulo
			and     c.nr_titulo = nr_titulo_p
			and     d.nr_sequencia = nr_seq_baixa_p
			and     exists (SELECT 1
					from  movto_trans_financ f
					where d.nr_seq_movto_trans_fin = f.nr_sequencia);

		elsif (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '')	then

			select	max(b.nr_conta)
			into STRICT	ds_retorno_w
			from	pessoa_fisica a,
				pessoa_fisica_conta b,
				titulo_receber c,
				titulo_receber_liq d
			where   a.cd_pessoa_fisica = b.cd_pessoa_fisica
			and     a.cd_pessoa_fisica = c.cd_pessoa_fisica
			and     c.nr_titulo = d.nr_titulo
			and     c.nr_titulo = nr_titulo_p
			and     d.nr_sequencia = nr_seq_baixa_p
			and     exists (SELECT 1
					from  movto_trans_financ f
					where d.nr_seq_movto_trans_fin = f.nr_sequencia);

			end if;


	elsif (ie_opcao_p = 'CONTAB') then -- conta do banco da movto_trans_financ
		select  max(b.cd_conta)
		into STRICT	ds_retorno_w
		from    movto_trans_financ a,
			banco_estabelecimento_v b,
			agencia_bancaria c,
			titulo_receber d,
			titulo_receber_liq e
		where   a.nr_seq_banco =  b.nr_sequencia
		and     e.nr_seq_movto_trans_fin = a.nr_sequencia
		and     b.cd_agencia_bancaria = c.cd_agencia_bancaria
		and     d.nr_titulo = e.nr_titulo
		and     d.nr_titulo = nr_titulo_p
		and     e.nr_sequencia = nr_seq_baixa_p;


	elsif (ie_opcao_p = 'RFCB') then  -- rfc da agencia bancaria do conta da movto_trans_financ
		select  max(obter_dados_pf_pj(null,c.cd_cgc_agencia,'RFC'))
		into STRICT	ds_retorno_w
		from    movto_trans_financ a,
			banco_estabelecimento_v b,
			agencia_bancaria c,
			titulo_receber d,
			titulo_receber_liq e
		where   a.nr_seq_banco =  b.nr_sequencia
		and     e.nr_seq_movto_trans_fin = a.nr_sequencia
		and     b.cd_agencia_bancaria = c.cd_agencia_bancaria
		and     d.nr_titulo = e.nr_titulo
		and     d.nr_titulo = nr_titulo_p
		and     e.nr_sequencia = nr_seq_baixa_p;

	end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
