SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_trans_financ ( nr_seq_trans_fin_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(250);

/*
N - Nome da Transação Financeira
TS - Tipo saldo da transação financeira
*/
BEGIN


if (nr_seq_trans_fin_p IS NOT NULL AND nr_seq_trans_fin_p::text <> '') then
	begin

	if (ie_opcao_p = 'N') then
		begin

		select	ds_transacao
		into STRICT	ds_retorno_w
		from	transacao_financeira
		where	nr_sequencia	= nr_seq_trans_fin_p;

		end;
	end if;

	if (ie_opcao_p = 'TS') then
		begin

		select	ie_saldo_caixa
		into STRICT	ds_retorno_w
		from	transacao_financeira
		where	nr_sequencia	= nr_seq_trans_fin_p;

		end;
	end if;


	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
