CREATE OR REPLACE FUNCTION obter_dados_treinamento (nr_seq_curso_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* Opção '1' - Descrição do Treinamento */

ds_retorno_w	varchar(255);
ds_curso_w	varchar(80);


BEGIN

select 	max(coalesce(ds_curso,0))
into STRICT	ds_curso_w
from 	tre_curso
where 	nr_sequencia = nr_seq_curso_p;


if (ie_opcao_p = '1') then
	ds_retorno_w:= ds_curso_w;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
