SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_trib_item_nf ( nr_sequencia_nf_p bigint, ie_tipo_tributo_p text, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


ie_tipo_tributo_w	varchar(15);
vl_base_calculo_w	double precision;
vl_retorno_w		double precision;
vl_total_nota_w		double precision;

/*
AL - Alíquota
BC - Valor da base de cálculo
O - Valor itens não tributados
TR - Valor do tributo
*/
BEGIN

ie_tipo_tributo_w := coalesce(ie_tipo_tributo_p,'X');

if (ie_opcao_p = 'TR') then

	select	coalesce(sum(n.vl_tributo),0)
	into STRICT	vl_retorno_w
	from	tributo t,
		nota_fiscal_item_trib n
	where	t.cd_tributo = n.cd_tributo
	and	n.nr_sequencia = nr_sequencia_nf_p
	and	((ie_tipo_tributo_w = 'X')
	or (t.ie_tipo_tributo = ie_tipo_tributo_w));

elsif (ie_opcao_p = 'AL') then

	select	max(n.tx_tributo)
	into STRICT	vl_retorno_w
	from	tributo t,
		nota_fiscal_item_trib n
	where	t.cd_tributo = n.cd_tributo
	and	n.nr_sequencia = nr_sequencia_nf_p
	and	((ie_tipo_tributo_w = 'X')
	or (t.ie_tipo_tributo = ie_tipo_tributo_w));

elsif (ie_opcao_p = 'BC') then

	select	coalesce(sum(n.vl_base_calculo),0)
	into STRICT	vl_retorno_w
	from	tributo t,
		nota_fiscal_item_trib n
	where	t.cd_tributo = n.cd_tributo
	and	n.nr_sequencia = nr_sequencia_nf_p
	and	((ie_tipo_tributo_w = 'X')
	or (t.ie_tipo_tributo = ie_tipo_tributo_w));

elsif (ie_opcao_p = 'O') then

	select	a.vl_total_nota
	into STRICT	vl_total_nota_w
	from	nota_fiscal a
	where	a.nr_sequencia = nr_sequencia_nf_p;

	select	coalesce(sum(n.vl_base_calculo),0)
	into STRICT	vl_base_calculo_w
	from	tributo t,
		nota_fiscal_item_trib n
	where	t.cd_tributo = n.cd_tributo
	and	n.nr_sequencia = nr_sequencia_nf_p
	and	((ie_tipo_tributo_w = 'X')
	or (t.ie_tipo_tributo = ie_tipo_tributo_w));

	vl_retorno_w := vl_total_nota_w - vl_base_calculo_w;

elsif (ie_opcao_p = 'VI') then

	select	sum(i.vl_total_item_nf)
	into STRICT	vl_retorno_w
	from	nota_fiscal_item i,
		nota_fiscal n
	where	n.nr_sequencia = i.nr_sequencia
	and	n.nr_sequencia = nr_sequencia_nf_p;

end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
