CREATE OR REPLACE FUNCTION obter_dados_trib_oc ( nr_ordem_compra_p bigint, ie_tributo_p text, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE

 
vl_tributo_w		double precision;
pr_tributo_w		double precision;
vl_retorno_w		double precision;


BEGIN 
 
select	max(a.vl_tributo), 
	max(a.pr_tributo) 
into STRICT	vl_tributo_w, 
	pr_tributo_w 
from	ordem_compra_v a, 
	tributo b 
where	a.nr_ordem_compra = nr_ordem_compra_p 
and	b.cd_tributo = a.cd_tributo 
and	b.ie_tipo_tributo = ie_tributo_p;
 
if (ie_opcao_p = 'V') then 
	vl_retorno_w	:= vl_tributo_w;
elsif (ie_opcao_p = 'P') then 
	vl_retorno_w	:= pr_tributo_w;
end if;
 
return	vl_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
