SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_ultima_aprovacao ( nr_documento_p bigint, ie_solic_ordem_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
/* ie_opcao 
D - Data definição da aprovacao 
U - Usuário aprovador 
*/
 
 
 
dt_definicao_w			varchar(30);
nm_usuario_w			varchar(15);
dt_definicao			timestamp;

c01 CURSOR FOR 
SELECT	to_char(a.dt_definicao,'dd/mm/yyyy hh24:mi:ss'), 
	a.nm_usuario,a.dt_definicao 
from	processo_aprov_compra a, 
	pessoa_fisica b 
where	a.cd_pessoa_fisica = b.cd_pessoa_fisica 
and	a.nr_sequencia in (SELECT distinct nr_seq_aprovacao 
	from	ordem_compra_item 
	where	nr_ordem_compra = nr_documento_p) 
and	(a.dt_definicao IS NOT NULL AND a.dt_definicao::text <> '') 
and	ie_solic_ordem_p = 'O' 

union all
 
select	to_char(a.dt_definicao,'dd/mm/yyyy hh24:mi:ss'), 
	a.nm_usuario,a.dt_definicao 
from	processo_aprov_compra a, 
	cargo b 
where	a.cd_cargo = b.cd_cargo 
and	a.nr_sequencia in (select distinct nr_seq_aprovacao 
	from ordem_compra_item 
where	nr_ordem_compra = nr_documento_p) 
and	(a.dt_definicao IS NOT NULL AND a.dt_definicao::text <> '') 
and	ie_solic_ordem_p = 'O' 

union all
 
select	to_char(a.dt_definicao,'dd/mm/yyyy hh24:mi:ss'), 
	a.nm_usuario,a.dt_definicao 
from	processo_aprov_compra a, 
	pessoa_fisica b 
where	a.cd_pessoa_fisica = b.cd_pessoa_fisica 
and	a.nr_sequencia in (select distinct nr_seq_aprovacao 
	from solic_compra_item 
	where nr_solic_compra = nr_documento_p) 
and	(a.dt_definicao IS NOT NULL AND a.dt_definicao::text <> '')	 
and	ie_solic_ordem_p = 'S' 

union all
 
select	to_char(a.dt_definicao,'dd/mm/yyyy hh24:mi:ss'), 
	a.nm_usuario,a.dt_definicao 
from	processo_aprov_compra a, 
	cargo b 
where	a.cd_cargo = b.cd_cargo 
and	a.nr_sequencia in (select	distinct nr_seq_aprovacao 
	from	solic_compra_item 
	where	nr_solic_compra = nr_documento_p) 
and	(a.dt_definicao IS NOT NULL AND a.dt_definicao::text <> '')	 
and	ie_solic_ordem_p = 'S' 
order by 3 asc;


BEGIN 
 
open C01;
loop 
fetch C01 into	 
	dt_definicao_w, 
	nm_usuario_w, 
	dt_definicao;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	dt_definicao_w	:= dt_definicao_w;
	nm_usuario_w	:= nm_usuario_w;
	end;
end loop;
close C01;
 
if (ie_opcao_p = 'D') then 
	return dt_definicao_w;
elsif (ie_opcao_p = 'U') then 
	return nm_usuario_w;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
