SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_ultima_npt ( nr_atendimento_p bigint, ie_opcao_p text ) RETURNS bigint AS $body$
DECLARE


qt_kcal_total_w		integer := 0;


BEGIN

if (ie_opcao_p = 'KCAL') then
	begin
	select	a.qt_kcal_total
	into STRICT	qt_kcal_total_w
	from	nut_paciente a
	where	a.nr_prescricao	=	(	SELECT	max(z.nr_prescricao)
						from	nut_paciente x,
							prescr_medica z
						where	x.nr_prescricao	=	z.nr_prescricao
						and 	z.dt_prescricao	between clock_timestamp() - (36 / 24)  and clock_timestamp() + interval '3 days'
						and	z.nr_atendimento = 	nr_atendimento_p);
	exception
	when others then
		qt_kcal_total_w	:= 0;
	end;
end if;

return	qt_kcal_total_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
