SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_ultimo_alerta (nr_atendimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(2000);

/*ie_opcao_p
D - Descrição do alerta.
T - Tipo do alerta.
DA - Data do alerta
*/
BEGIN

if (ie_opcao_p = 'D') then
	begin
	select 	a.ds_alerta
	into STRICT	ds_retorno_w
	from 	atendimento_alerta a
	where 	a.nr_atendimento = nr_atendimento_p
	and 	a.ie_situacao = 'A'
	and 	a.nr_sequencia = (SELECT max(b.nr_sequencia) from atendimento_alerta b where a.nr_atendimento = b.nr_atendimento);

	end;
elsif (ie_opcao_p = 'T') then
	begin
	select	a.ds_tipo_alerta
	into STRICT	ds_retorno_w
	from	tipo_alerta_atend a
	where	a.nr_sequencia = (SELECT b.nr_seq_tipo_alerta
		from 	atendimento_alerta b
		where 	b.nr_atendimento = nr_atendimento_p
		and 	b.ie_situacao = 'A'
		and 	b.nr_sequencia = (select max(c.nr_sequencia) from atendimento_alerta c where c.nr_atendimento = b.nr_atendimento));
	end;
elsif (ie_opcao_p = 'DA') then
	begin
	select	max(a.dt_alerta)
	into STRICT	ds_retorno_w
	from	atendimento_alerta a
	where	a.nr_atendimento	= nr_atendimento_p
	and	a.ie_situacao	= 'A'
	and	a.nr_sequencia	= (SELECT	max(b.nr_sequencia) from atendimento_alerta b where a.nr_atendimento	= b.nr_atendimento);

	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
