SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_ultimo_atend_dt ( cd_pessoa_fisica_p text, nr_atendimento_p bigint, ie_tipo_atend_p bigint, ie_opcao_p text) RETURNS timestamp AS $body$
DECLARE


/* ie_opcao_p :

  - DA - data de alta
  - DE - data de entrada
  - DAF - data ultimo atendimento (ficha financeira).  
		  
*/
nr_atendimento_w	bigint;
dt_retorno_w		timestamp := null;


BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
	begin
	nr_atendimento_w	:= null;
	if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
		select 	max(nr_atendimento)
		into STRICT	nr_atendimento_w
		from 	atendimento_paciente
		where 	nr_atendimento		< nr_atendimento_p
		and	cd_pessoa_fisica	= cd_pessoa_fisica_p
		and 	ie_tipo_atendimento	= coalesce(ie_tipo_atend_p, ie_tipo_atendimento);
	end if;
	end;
	
elsif (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	begin
	if (coalesce(ie_tipo_atend_p::text, '') = '') then
		select coalesce(max(nr_atendimento),null)
		into STRICT	nr_atendimento_w
		from atendimento_paciente
		where cd_pessoa_fisica		= cd_pessoa_fisica_p;
	else
		select coalesce(max(nr_atendimento),null)
		into STRICT	nr_atendimento_w
		from atendimento_paciente
		where cd_pessoa_fisica		= cd_pessoa_fisica_p
		and ie_tipo_atendimento	= coalesce(ie_tipo_atend_p, ie_tipo_atendimento);
	end if;
	end;
end if;

if (nr_atendimento_w IS NOT NULL AND nr_atendimento_w::text <> '') then

	if (ie_opcao_p = 'DA') then
	
		select max(dt_alta)
		into STRICT dt_retorno_w
		from atendimento_paciente
		where nr_atendimento = nr_atendimento_w;
		
	elsif (ie_opcao_p = 'DE') then
	
		select max(dt_entrada)
		into STRICT dt_retorno_w
		from atendimento_paciente
		where nr_atendimento = nr_atendimento_w;
		
	elsif (ie_opcao_p = 'DAF') then
		
		select max(dt_alta)
		into STRICT dt_retorno_w
		from atendimento_paciente
		where nr_atendimento = nr_atendimento_w;
	
	end if;
end if;

RETURN dt_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
