SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_ultimo_atend ( cd_pessoa_fisica_p text, nr_atendimento_p bigint, ie_tipo_atend_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
/* ie_opcao_p - NA - nº atendimento 
		 - DA - data de alta 
		 - DE - data de entrada 
		 - TA - tipo de atendimento 
		 - ME - Médico Resp 
		 - CO - Convênio 
		 -DAF - data ultimo atendimento (ficha financeira). 
*/
 
 
nr_atendimento_w		bigint;
ds_retorno_w		varchar(255) := '';


BEGIN 
 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then 
	begin 
	nr_atendimento_w	:= null;
	if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then 
		select 	max(nr_atendimento) 
		into STRICT	nr_atendimento_w 
		from 	atendimento_paciente 
		where 	nr_atendimento		< nr_atendimento_p 
		and	cd_pessoa_fisica	= cd_pessoa_fisica_p 
		and 	ie_tipo_atendimento	= coalesce(ie_tipo_atend_p, ie_tipo_atendimento);
	end if;
	end;
elsif (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then 
	begin 
	if (coalesce(ie_tipo_atend_p::text, '') = '') then 
		select coalesce(max(nr_atendimento),null) 
		into STRICT	nr_atendimento_w 
		from atendimento_paciente 
		where cd_pessoa_fisica		= cd_pessoa_fisica_p;
	else 
		select coalesce(max(nr_atendimento),null) 
		into STRICT	nr_atendimento_w 
		from atendimento_paciente 
		where cd_pessoa_fisica		= cd_pessoa_fisica_p 
		and ie_tipo_atendimento	= coalesce(ie_tipo_atend_p, ie_tipo_atendimento);
	end if;
	end;
end if;
 
if (nr_atendimento_w IS NOT NULL AND nr_atendimento_w::text <> '') then 
	if (ie_opcao_p = 'NA') then 
		ds_retorno_w	:= nr_atendimento_w;
	elsif (ie_opcao_p = 'DA') then 
		select to_char(dt_alta, 'dd/mm/yyyy hh24:mi:ss') 
		into STRICT ds_retorno_w 
		from atendimento_paciente 
		where nr_atendimento = nr_atendimento_w;
	elsif (ie_opcao_p = 'DE') then 
		select to_char(dt_entrada, 'dd/mm/yyyy hh24:mi:ss') 
		into STRICT ds_retorno_w 
		from atendimento_paciente 
		where nr_atendimento = nr_atendimento_w;
	elsif (ie_opcao_p = 'TA') then 
		select ie_tipo_atendimento 
		into STRICT ds_retorno_w 
		from atendimento_paciente 
		where nr_atendimento = nr_atendimento_w;
	elsif (ie_opcao_p = 'ME') then 
		select obter_nome_pf(cd_medico_resp) 
		into STRICT ds_retorno_w 
		from atendimento_paciente 
		where nr_atendimento = nr_atendimento_w;
	elsif (ie_opcao_p = 'CO') then 
		select ds_convenio 
		into STRICT ds_retorno_w 
		from atendimento_paciente_v 
		where nr_atendimento = nr_atendimento_w;
	elsif (ie_opcao_p = 'CL') then 
		select	substr(obter_valor_dominio(17, ie_clinica),1,255) 
		into STRICT	ds_retorno_w 
		from	atendimento_paciente 
		where	nr_atendimento = nr_atendimento_w;
	elsif (ie_opcao_p = 'DAF') then 
		select max(coalesce(to_char(dt_alta, 'dd/mm/yyyy hh24:mi:ss'), wheb_mensagem_pck.get_texto(301738))) 
		into STRICT ds_retorno_w 
		from atendimento_paciente 
		where nr_atendimento = nr_atendimento_w;
	end if;
end if;
 
RETURN ds_retorno_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
