SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_ultimo_retorno ( nr_interno_conta_p bigint, cd_autorizacao_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
nr_seq_retorno_w	bigint;
vl_amenor_w		double precision;
vl_pago_w		double precision;
dt_retorno_w		timestamp;
ds_observacao_w		varchar(255);


BEGIN

vl_amenor_w	:= 0;

select 	coalesce(max(nr_seq_retorno),0)
into STRICT	nr_seq_retorno_w
from	convenio_retorno_item
where	nr_interno_conta = nr_interno_conta_p
and	cd_autorizacao = cd_autorizacao_p;

if (nr_seq_retorno_w > 0) then
	select 	coalesce(sum(b.vl_amenor),0),
		coalesce(sum(b.vl_pago),0),
		max(a.dt_retorno),
		substr(max(b.ds_observacao),1,255)
	into STRICT	vl_amenor_w,
		vl_pago_w,
		dt_retorno_w,
		ds_observacao_w
	from	convenio_retorno a,
		convenio_retorno_item b
	where	a.nr_sequencia		= b.nr_seq_retorno
	and	b.nr_seq_retorno = nr_seq_retorno_w
	and	b.nr_interno_conta = nr_interno_conta_p
	and	b.cd_autorizacao = cd_autorizacao_p;
end if;

if (ie_opcao_p = 'M') then
	ds_retorno_w := vl_amenor_w;
elsif (ie_opcao_p = 'P') then
	ds_retorno_w := vl_pago_w;
elsif (ie_opcao_p = 'DT') then
	ds_retorno_w := dt_retorno_w;
elsif (ie_opcao_p = 'O') then
	ds_retorno_w := ds_observacao_w;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
