CREATE OR REPLACE FUNCTION obter_dados_ult_resp_bol_ocor ( nr_seq_bo_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
DT - Descrição tipo
*/
ds_retorno_w		varchar(255);
nr_seq_tipo_w		bigint;
ds_tipo_ocorrencia_w	varchar(50);
ie_status_w		varchar(1);


BEGIN

select	max(nr_seq_tipo),
	max(ie_status)
into STRICT	nr_seq_tipo_w,
	ie_status_w
from	sac_resp_bol_ocor
where	nr_seq_bo	= nr_seq_bo_p
and	dt_resposta	= 	(SELECT	max(dt_resposta)
				from	sac_resp_bol_ocor
				where	nr_seq_bo	= nr_seq_bo_p);

if (ie_opcao_p = 'DT') then
	begin
	SELECT 	ds_tipo_ocorrencia
	into STRICT	ds_tipo_ocorrencia_w
	FROM	sac_tipo_ocorrencia
	WHERE	nr_sequencia = nr_seq_tipo_w;
	ds_retorno_w := ds_tipo_ocorrencia_w;
	end;
elsif (ie_opcao_p = 'DS') then
	begin
	select	substr(obter_valor_dominio(1079,ie_status_w),1,150)
	into STRICT	ds_retorno_w
	;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_dados_ult_resp_bol_ocor ( nr_seq_bo_p bigint, ie_opcao_p text) FROM PUBLIC;

