SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_unidade ( cd_setor_atendimento_p bigint, cd_unidade_basica_p text, cd_unidade_compl_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w varchar(4000) :=NULL;

/*
IE_OPCAO_P:
'NSUB' 	- Nome Setor + Cd_Unidade_Basica
'NSUBUC'	- Nome setor + Cd unidade basica + cd unidade compl
'NRSI'	- NR_SEQ_INTERNO
*/
BEGIN

IF ie_opcao_p = 'NSUB' THEN
	IF NOT(coalesce(cd_setor_atendimento_p::text, '') = '') AND
	   NOT(coalesce(cd_unidade_basica_p::text, '') = '') THEN
		SELECT 	MAX(obter_nome_setor(cd_setor_atendimento)||' - '||cd_unidade_basica)
		INTO STRICT	ds_retorno_w
		FROM	unidade_atendimento
		WHERE	cd_setor_atendimento 	= cd_setor_atendimento_p
		AND	UPPER(cd_unidade_basica)= UPPER(cd_unidade_basica_p);
		RETURN ds_retorno_w;
	END IF;
elsif ie_opcao_p = 'NSUBUC' then
	IF NOT(coalesce(cd_setor_atendimento_p::text, '') = '') AND
	   NOT(coalesce(cd_unidade_basica_p::text, '') = '') and
	   NOT(coalesce(cd_unidade_compl_p::text, '') = '') THEN
		SELECT 	MAX(obter_nome_setor(cd_setor_atendimento)||' - ('||cd_unidade_basica||'-'||cd_unidade_compl||')')
		INTO STRICT	ds_retorno_w
		FROM	unidade_atendimento
		WHERE	cd_setor_atendimento 	= cd_setor_atendimento_p
		AND	UPPER(cd_unidade_basica)= UPPER(cd_unidade_basica_p)
		AND	UPPER(cd_unidade_compl)= UPPER(cd_unidade_compl_p);
		RETURN ds_retorno_w;
	END IF;
elsif ie_opcao_p = 'DTC' then
	IF NOT(coalesce(cd_setor_atendimento_p::text, '') = '') AND
	   NOT(coalesce(cd_unidade_basica_p::text, '') = '') and
	   NOT(coalesce(cd_unidade_compl_p::text, '') = '') THEN
		SELECT 	MAX(DT_CRIACAO)
		INTO STRICT	ds_retorno_w
		FROM	unidade_atendimento
		WHERE	cd_setor_atendimento 	= cd_setor_atendimento_p
		AND	UPPER(cd_unidade_basica)= UPPER(cd_unidade_basica_p)
		AND	UPPER(cd_unidade_compl)= UPPER(cd_unidade_compl_p);
		RETURN ds_retorno_w;
	END IF;
elsif ie_opcao_p = 'NRSI' then
	IF NOT(coalesce(cd_setor_atendimento_p::text, '') = '') AND
	   NOT(coalesce(cd_unidade_basica_p::text, '') = '') and
	   NOT(coalesce(cd_unidade_compl_p::text, '') = '') THEN
		SELECT	coalesce(MAX(NR_SEQ_INTERNO),0)
		INTO STRICT	ds_retorno_w
		FROM	unidade_atendimento
		WHERE	cd_setor_atendimento 	= cd_setor_atendimento_p
		AND	UPPER(cd_unidade_basica)= UPPER(cd_unidade_basica_p)
		AND	UPPER(cd_unidade_compl)= UPPER(cd_unidade_compl_p);
		RETURN ds_retorno_w;
	END IF;
END IF;

RETURN	ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
