CREATE OR REPLACE FUNCTION obter_dados_unid_medida ( cd_unidade_medida_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
ie_opcao_p
SA	CD_SISTEMA_ANT
DS  Descrição da unidade de medida
*/
cd_sistema_ant_w			varchar(255);
ds_retorno_w			varchar(255);
ds_unidade_medida_w		varchar(255);


BEGIN

select	max(cd_sistema_ant),
		max(ds_unidade_medida)
into STRICT	cd_sistema_ant_w,
		ds_unidade_medida_w
from	unidade_medida
where	cd_unidade_medida = cd_unidade_medida_p;

if (ie_opcao_p = 'SA') then
	ds_retorno_w := cd_sistema_ant_w;
elsif (ie_opcao_p = 'DS') then
	ds_retorno_w := ds_unidade_medida_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
