CREATE OR REPLACE FUNCTION obter_dados_uti_atend ( nr_atendimento_p bigint, dt_inicial_p timestamp, dt_final_p timestamp, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(20);

/* ie_opcao_p

 Q - Número de vezes que o paciente passou por um setor de UTI.
 E - Data da primeira passagem pelo setor de UTI.
 S - Data de alta da última passagem pelo setor de UTI.
*/
BEGIN

if (ie_opcao_p = 'Q') then
	select	count(*)
	into STRICT	ds_retorno_w
	from 	atend_paciente_unidade
	where	nr_atendimento		= nr_atendimento_p
	and	obter_classif_setor(cd_setor_atendimento) = 4
	and	((dt_entrada_unidade	between dt_inicial_p and dt_final_p) or (dt_saida_unidade	between dt_inicial_p and dt_final_p));

elsif (ie_opcao_p = 'E') then
	select	min(to_char(dt_entrada_unidade,'dd/mm/yyyy'))
	into STRICT	ds_retorno_w
	from 	atend_paciente_unidade
	where	nr_atendimento		= nr_atendimento_p
	and	obter_classif_setor(cd_setor_atendimento) = 4
	and	((dt_entrada_unidade	between dt_inicial_p and dt_final_p) or (dt_saida_unidade	between dt_inicial_p and dt_final_p));

elsif (ie_opcao_p = 'S') then
	select	max(to_char(dt_saida_unidade,'dd/mm/yyyy'))
	into STRICT	ds_retorno_w
	from 	atend_paciente_unidade
	where	nr_atendimento		= nr_atendimento_p
	and	obter_classif_setor(cd_setor_atendimento) = 4
	and	((dt_entrada_unidade	between dt_inicial_p and dt_final_p) or (dt_saida_unidade	between dt_inicial_p and dt_final_p));
end if;


return ds_retorno_w;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
