CREATE OR REPLACE FUNCTION obter_dados_vaga_mapa ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(2000);
nr_seq_vaga_w		gestao_vaga.nr_sequencia%type;
dt_solicitacao_w	gestao_vaga.dt_solicitacao%type;
dt_prevista_w		gestao_vaga.dt_prevista%type;
ie_status_w		gestao_vaga.ie_status%type;
ds_tipo_acomodacao_w	tipo_acomodacao.ds_tipo_acomodacao%type;
ds_setor_desejado_w	setor_atendimento.ds_setor_atendimento%type;
cd_unidade_desejada_w	varchar(255);

BEGIN

ds_retorno_w	:= '';

select	max(nr_sequencia)
into STRICT	nr_seq_vaga_w
from	gestao_vaga
where	nr_atendimento	= nr_atendimento_p
and	ie_status not in ('F','C','D','N','E','T');
/*
F = Acomodado
C = Cancelada
D = Desistiu
N = Negada
E = Transferência recusada pelo paciente
T = Transferência realizada
*/
if (nr_seq_vaga_w IS NOT NULL AND nr_seq_vaga_w::text <> '') then
	select	max(dt_solicitacao),
		max(dt_prevista),
		max(ie_status),
		max(obter_desc_tipo_acomodacao(cd_tipo_acomod_desej)) ds_tipo_acomodacao,
		max(obter_ds_descricao_setor(cd_setor_desejado)) ds_setor_desejado,
		max(cd_unidade_basica||' '||cd_unidade_compl)
	into STRICT	dt_solicitacao_w,
		dt_prevista_w,
		ie_status_w,
		ds_tipo_acomodacao_w,
		ds_setor_desejado_w,
		cd_unidade_desejada_w
	from	gestao_vaga
	where	nr_sequencia = nr_seq_vaga_w;

	if (ie_status_w = 'R') then
		ds_retorno_w	:= obter_desc_expressao(697779) || ': '; -- Leito liberado
	else
		ds_retorno_w	:= obter_desc_expressao(697775) || ': '; -- Leito requisitado
	end if;

	ds_retorno_w	:= substr(ds_retorno_w || ds_setor_desejado_w,0,1000);

	if (cd_unidade_desejada_w IS NOT NULL AND cd_unidade_desejada_w::text <> '') then
		ds_retorno_w	:= substr(ds_retorno_w || ' - ' || cd_unidade_desejada_w,0,1000);
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
