SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_versao_exec_teste ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*	ie_opcao_p
	P - Sequência da previsão de atualização
	V - Versão				*/
nr_seq_caso_teste_w	bigint;
nr_seq_prev_atual_w	bigint;
nr_versao_w		varchar(50);
ds_retorno_w		varchar(255);


BEGIN

if (coalesce(nr_sequencia_p,0) > 0) then
	begin

	begin
	select	x.nr_sequencia
	into STRICT	nr_seq_caso_teste_w
	from (SELECT	a.nr_sequencia
		from	teste_soft_exec_acao b,
			teste_caso_teste a
		where	a.nr_sequencia		= b.nr_seq_caso_teste
		and	b.nr_seq_teste_software	= nr_sequencia_p
		and	(a.nr_seq_aplicacao IS NOT NULL AND a.nr_seq_aplicacao::text <> '')
		order by b.dt_atualizacao_nrec desc,
			b.nr_sequencia desc) x LIMIT 1;
	exception
	when others then
		nr_seq_caso_teste_w := 0;
	end;

	if (nr_seq_caso_teste_w > 0) then
		begin

		begin
		select	x.nr_sequencia
		into STRICT	nr_seq_prev_atual_w
		from (SELECT	b.nr_sequencia
			from	atualizacao_tasy b,
				teste_caso_teste a
			where	a.nr_seq_aplicacao		= b.nr_seq_aplicacao
			and	a.nr_sequencia		= nr_seq_caso_teste_w
			and	b.ie_status		= 'A'
			and	b.cd_estabelecimento	= coalesce(wheb_usuario_pck.get_cd_estabelecimento,b.cd_estabelecimento)
			order by b.dt_prevista_atualizacao desc) x LIMIT 1;
		exception
		when others then
			nr_seq_prev_atual_w := 0;
		end;

		if (nr_seq_prev_atual_w > 0) then
			begin
			select	a.nr_versao
			into STRICT	nr_versao_w
			from	atualizacao_tasy a
			where	a.nr_sequencia = nr_seq_prev_atual_w;

			if (ie_opcao_p = 'P') then
				ds_retorno_w := nr_seq_prev_atual_w;
			elsif (ie_opcao_p = 'V') then
				ds_retorno_w := nr_versao_w;
			end if;
			end;
		end if;
		end;
	end if;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_dados_versao_exec_teste ( nr_sequencia_p bigint, ie_opcao_p text) FROM PUBLIC;

