SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_viagem ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
/* 
ie_opcao_p 
 
'NP' - Nome da pessoa 
'SQ' - Sequencia da viagem	 
*/
 
 
 
ds_retorno_w	varchar(255);
nm_pessoa_w	varchar(60);
nr_sequencia_w	bigint;


BEGIN 
 
if (nr_sequencia_p > 0) then 
	select 	substr(obter_nome_pf(cd_pessoa_fisica),1,60), 
		nr_sequencia 
	into STRICT	nm_pessoa_w, 
		nr_sequencia_w 
	from 	via_viagem 
	where 	nr_sequencia = nr_sequencia_p;
end if;
 
if (ie_opcao_p	= 'NP') then 
	ds_retorno_w	:= nm_pessoa_w;
elsif (ie_opcao_p	= 'SQ') then 
	ds_retorno_w	:= to_char(nr_sequencia_w);
end if;
 
return ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
