SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_via_aplicacao (cd_material_p bigint, ie_via_aplicacao_p text, cd_setor_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_recomendacao_w	varchar(2000);
ds_retorno_w		varchar(2000);


BEGIN

select 	max(ds_recomendacao)
into STRICT	ds_recomendacao_w
from	mat_via_aplic
where	cd_material = cd_material_p
and	((coalesce(cd_setor_atendimento::text, '') = '') or (cd_setor_atendimento = cd_setor_p))
and	((coalesce(cd_setor_excluir::text, '') = '') or (cd_setor_excluir <> cd_setor_p))
and	ie_via_aplicacao = ie_via_aplicacao_p
and	coalesce(cd_estabelecimento,wheb_usuario_pck.get_cd_estabelecimento) = wheb_usuario_pck.get_cd_estabelecimento;

if (ie_opcao_p = 'R') then
	ds_retorno_w	:= substr(ds_recomendacao_w,1,2000);
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
