CREATE OR REPLACE FUNCTION obter_dados_w_cot_item_rel ( nr_cot_compra_p bigint, cd_material_p bigint, cd_estabelecimento_p bigint, nr_seq_cot_rel_p bigint, ie_tipo_retorno_p text) RETURNS bigint AS $body$
DECLARE


qt_registros_w		smallint := 0;
vl_fornecedor_w		double precision;
vl_menor_w		double precision;
vl_media_w		double precision;
vl_total_w		double precision := 0;


c01 CURSOR FOR
SELECT	vl_fornecedor
from	w_cot_item_coluna_rel a,
	w_cot_item_rel b
where	a.nr_seq_cotacao_item = b.nr_sequencia
and	a.nr_seq_coluna in (1,2,3,4,5,6)
and	a.nr_cot_compra = nr_cot_compra_p
and	b.nr_sequencia = nr_seq_cot_rel_p
and	b.cd_material = cd_material_p
and	b.cd_estabelecimento = cd_estabelecimento_p;


BEGIN

open c01;
loop
fetch c01 into
	vl_fornecedor_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	if (vl_fornecedor_w > 0) then
		qt_registros_w	:= (qt_registros_w + 1);
		vl_total_w	:= (vl_total_w + vl_fornecedor_w);
		if (coalesce(vl_menor_w,999999999) > vl_fornecedor_w) then
			vl_menor_w	:= (vl_fornecedor_w);
		end if;
	end if;
	end;
end loop;
close c01;

if (qt_registros_w > 0) and (vl_total_w > 0) then
	vl_media_w := (vl_total_w/qt_registros_w);
end if;

if (ie_tipo_retorno_p = 'MV') then
	return	coalesce(vl_menor_w,0);
elsif (ie_tipo_retorno_p = 'ME') then
	return	coalesce(vl_media_w,0);
end if;

end;
$body$
