CREATE OR REPLACE FUNCTION obter_dados_w_razao_estoque (cd_material_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/* Ie_opcao_p
SAN - Saldo Anterior
SAT - Saldo Atual
VAN - Valor Estoque Anterior
VAT - Valor Estoque Atual
*/
qt_anterior_w 	double precision;
qt_atual_w 	double precision;
vl_anterior_w 	double precision;
vl_atual_w	double precision;


BEGIN
select	coalesce(qt_estoque,0),
	coalesce(vl_estoque,0)
into STRICT	qt_anterior_w,
	vl_anterior_w
from 	w_razao_estoque
where 	cd_material = cd_material_p
and 	ie_tipo_informacao = 0;

select	coalesce(qt_estoque,0),
	coalesce(vl_estoque,0)
into STRICT	qt_atual_w,
	vl_atual_w
from 	w_razao_estoque
where 	cd_material = cd_material_p
and 	ie_tipo_informacao = 9;

if (ie_opcao_p = 'SAN') then
	return qt_anterior_w;
end if;
if (ie_opcao_p = 'SAT') then
	return qt_atual_w;
end if;
if (ie_opcao_p = 'VAN') then
	return vl_anterior_w;
end if;
if (ie_opcao_p = 'VAT') then
	return vl_atual_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
