CREATE OR REPLACE FUNCTION obter_dado_clinico_proced (nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w		varchar(2000):= '';
ds_dado_clinico_w	varchar(255);

c01 CURSOR FOR
	SELECT	substr(ds_dado_clinico,1,255)
	from	prescr_procedimento
	where	nr_prescricao	= nr_prescricao_p;


BEGIN

open c01;
loop
fetch	c01 into
	ds_dado_clinico_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

if (ds_dado_clinico_w IS NOT NULL AND ds_dado_clinico_w::text <> '') then
	ds_retorno_w := substr((ds_retorno_w || ds_dado_clinico_w || ' '),1,255);
end if;

end loop;
close c01;

return	substr(ds_retorno_w,1,1999);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
