CREATE OR REPLACE FUNCTION obter_dado_medida_laudo ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_resultado_w		varchar(255);


BEGIN

if (ie_opcao_p	= 'M') then
	select	ds_medida
	into STRICT	ds_resultado_w
	from medida_exame_laudo
	where nr_sequencia = nr_sequencia_p;
elsif (ie_opcao_p	= 'F') then
	select	ds_formula
	into STRICT	ds_resultado_w
	from medida_exame_laudo
	where nr_sequencia = nr_sequencia_p;
elsif (ie_opcao_p	= 'U') then
	select	ds_unidade_medida
	into STRICT	ds_resultado_w
	from medida_exame_laudo
	where nr_sequencia = nr_sequencia_p;
elsif (ie_opcao_p	= 'I') then
	select	ie_informado
	into STRICT	ds_resultado_w
	from medida_exame_laudo
	where nr_sequencia = nr_sequencia_p;
elsif (ie_opcao_p	= 'S') then
	select	nr_seq_tipo_medida
	into STRICT	ds_resultado_w
	from medida_exame_laudo
	where nr_sequencia = nr_sequencia_p;
elsif (ie_opcao_p	= 'Q') then
	select	max(qt_decimais)
	into STRICT	ds_resultado_w
	from medida_exame_laudo
	where nr_sequencia = nr_sequencia_p;
end if;

return ds_resultado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
