CREATE OR REPLACE FUNCTION obter_dado_pre_venda ( nr_pre_venda_p bigint, ie_tipo_dado_p text) RETURNS varchar AS $body$
DECLARE

ds_retorno_w		varchar(4000);

BEGIN
if (nr_pre_venda_p IS NOT NULL AND nr_pre_venda_p::text <> '') and (ie_tipo_dado_p IS NOT NULL AND ie_tipo_dado_p::text <> '') then
	begin
	if (ie_tipo_dado_p = 'NOV') then
		begin
		select	nr_ordem_venda
		into STRICT	ds_retorno_w
		from	pre_venda
		where	nr_sequencia = nr_pre_venda_p;
		end;
	elsif (ie_tipo_dado_p = 'NPJ') then
		begin
		select obter_nome_pf_pj(null, cd_cnpj_empresa)
		into STRICT ds_retorno_w
		from pre_venda
		where nr_sequencia = nr_pre_venda_p;
		end;
	elsif (ie_tipo_dado_p = 'AGR') then
		begin
		select ie_agrupada
		into STRICT ds_retorno_w
		from pre_venda
		where nr_sequencia = nr_pre_venda_p;
		end;
	end if;	
	end;
end if;

return	ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
