SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_datas_pf ( cd_pessoa_fisica_p text, ie_opcao_p text) RETURNS timestamp AS $body$
DECLARE


/* IE_OPCAO_P:
	DN - Data de nascimento
	DO - Data de obito
	DA - Data de adocao
	DRG - Data de emissao da carteira de identidade
	VRG - Data de validade da carteira de identidade
	DCB - Data chegada Brasil
	DNAT - Data da naturalizacao
	ECTPS- Data de emissao do CTPS
	DEC - Data de emissao da certidao de casamento
	DPRA - Data do primeiro atendimento
*/
dt_retorno_w		timestamp;
nr_atendimento_w	atendimento_paciente.nr_atendimento%type;
			

BEGIN

if (coalesce(cd_pessoa_fisica_p,'0') <> '0') then
	begin	
	if (ie_opcao_p = 'DN') then -- Data de nascimento
		select	to_char(max(dt_nascimento),'DD/MM/YYYY')
		into STRICT	dt_retorno_w
		from	pessoa_fisica
		where	cd_pessoa_fisica = cd_pessoa_fisica_p;
	elsif (ie_opcao_p = 'DO') then -- Data de obito
		select	to_char(max(dt_obito),'DD/MM/YYYY')
		into STRICT	dt_retorno_w
		from	pessoa_fisica
		where	cd_pessoa_fisica = cd_pessoa_fisica_p;
	elsif (ie_opcao_p = 'DA') then -- Data de adocao
		select	max(dt_adocao)
		into STRICT	dt_retorno_w
		from	pessoa_fisica
		where	cd_pessoa_fisica = cd_pessoa_fisica_p;
	elsif (ie_opcao_p = 'DRG') then -- Data de emissao da carteira de identidade
		select	to_char(max(dt_emissao_ci), 'dd/mm/yyyy')
		into STRICT	dt_retorno_w
		from	pessoa_fisica
		where	cd_pessoa_fisica = cd_pessoa_fisica_p;
	elsif (ie_opcao_p = 'VRG') then -- Data de validade da carteira de identidade
		select	to_char(max(dt_validade_rg), 'dd/mm/yyyy')
		into STRICT	dt_retorno_w
		from	pessoa_fisica
		where	cd_pessoa_fisica = cd_pessoa_fisica_p;
	elsif (ie_opcao_p = 'DCB') then -- Data chegada Brasil
		select	max(dt_chegada_brasil)
		into STRICT	dt_retorno_w
		from	pessoa_fisica
		where	cd_pessoa_fisica = cd_pessoa_fisica_p;
	elsif (ie_opcao_p = 'DNAT') then -- Data da naturalizacao
		select	max(dt_naturalizacao_pf)
		into STRICT	dt_retorno_w
		from	pessoa_fisica
		where	cd_pessoa_fisica = cd_pessoa_fisica_p;
	elsif (ie_opcao_p = 'ECTPS') then -- Data de emissao do CTPS
		select	max(dt_emissao_ctps)
		into STRICT	dt_retorno_w
		from	pessoa_fisica
		where	cd_pessoa_fisica = cd_pessoa_fisica_p;
	elsif (ie_opcao_p = 'DEC') then -- Data de emissao da certidao de casamento
		select	max(dt_emissao_cert_casamento)
		into STRICT	dt_retorno_w
		from	pessoa_fisica
		where	cd_pessoa_fisica = cd_pessoa_fisica_p;
	elsif (ie_opcao_p = 'DPRA') then -- Data do primeiro atendimento
		begin
		select	min(nr_atendimento)
		into STRICT	nr_atendimento_w
		from	atendimento_paciente
		where	cd_pessoa_fisica = cd_pessoa_fisica_p;

		select  min(dt_entrada)
		into STRICT 	dt_retorno_w
		from	atendimento_paciente
		where	nr_atendimento = nr_atendimento_w;
		end;
	end if;
	end;
end if;

return	dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_datas_pf ( cd_pessoa_fisica_p text, ie_opcao_p text) FROM PUBLIC;

