CREATE OR REPLACE FUNCTION obter_datas_solic_compra ( nr_solic_compra_p bigint, ie_opcao_p text) RETURNS timestamp AS $body$
DECLARE

 
/*ie_opcao_p 
S - dt_solicitacao_compra 
A - dt_autorizacao_compra 
L - dt_liberacao_compra 
*/
 
 
dt_retorno_w		timestamp;
dt_solicitacao_compra_w	timestamp;
dt_autorizacao_w	timestamp;
dt_liberacao_w		timestamp;


BEGIN 
 
select	dt_solicitacao_compra, 
	dt_autorizacao, 
	dt_liberacao 
into STRICT	dt_solicitacao_compra_w, 
	dt_autorizacao_w, 
	dt_liberacao_w 
from	solic_compra 
where	nr_solic_compra = nr_solic_compra_p;
 
if (ie_opcao_p = 'S') then 
	dt_retorno_w :=	dt_solicitacao_compra_w;
elsif (ie_opcao_p = 'A') then 
	dt_retorno_w :=	dt_autorizacao_w;
elsif (ie_opcao_p = 'L') then 
	dt_retorno_w :=	dt_liberacao_w;
end if;
 
return dt_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
