CREATE OR REPLACE FUNCTION obter_datas_tit_rec_liq_cc ( nr_titulo_p bigint, nr_seq_baixa_p bigint) RETURNS timestamp AS $body$
DECLARE

dt_retorno_w		timestamp;


BEGIN 
if (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') and (coalesce(nr_seq_baixa_p::text, '') = '') then 
	select	c.dt_mesano_referencia 
	into STRICT	dt_retorno_w 
	from	titulo_receber		b, 
		pls_mensalidade		a, 
		pls_lote_mensalidade	c 
	where	b.nr_seq_mensalidade	= a.nr_sequencia 
	and	a.nr_seq_lote		= c.nr_sequencia 
	and	b.nr_titulo	 	= nr_titulo_p;
else 
	select	a.dt_antecipacao_pls_mens 
	into STRICT	dt_retorno_w 
	from	titulo_receber_liq	a 
	where	a.nr_sequencia	= nr_seq_baixa_p 
	and	a.nr_titulo	= nr_titulo_p;
end if;
 
return	dt_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
