CREATE OR REPLACE FUNCTION obter_datas_versao_corrente ( ie_opcao_p text ) RETURNS timestamp AS $body$
DECLARE


/*
	IE_OPCAO_P

	I - DT_INICIO
	F - DT_FIM

*/
dt_versao_w			timestamp;


BEGIN

if (ie_opcao_p = 'I') then

	select	max(dt_inicio)
	into STRICT	dt_versao_w
	from	escala_diaria d
	where	d.nr_seq_escala = 21
	and	clock_timestamp() between dt_inicio and dt_fim;

elsif (ie_opcao_p = 'F') then

	select	max(dt_inicio) + 14
	into STRICT	dt_versao_w
	from	escala_diaria d
	where	d.nr_seq_escala = 21
	and	clock_timestamp() between dt_inicio and dt_fim;

end if;

return	dt_versao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
