SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_data_adm_chegada ( nr_sequencia_p bigint, nr_seq_atendimento_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_retorno_w			timestamp := null;
qt_min_aplic_w			double precision;


BEGIN

select	sum(coalesce(a.qt_hora_aplic,0) * 60) + sum(coalesce(a.qt_min_aplic,0))
into STRICT	qt_min_aplic_w
from	can_ordem_prod a
where	a.nr_seq_atendimento	= nr_seq_atendimento_p
and	a.nr_sequencia 		< nr_sequencia_p;


select 	max(a.dt_chegada)
into STRICT 	dt_retorno_w
from 	paciente_atendimento a,
	can_ordem_prod b
where   b.nr_sequencia			= nr_sequencia_p
and	a.nr_seq_atendimento 		= b.nr_seq_atendimento;

dt_retorno_w := dt_retorno_w + (coalesce(qt_min_aplic_w,0) / 1440);

return	dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
