CREATE OR REPLACE FUNCTION obter_data_adm_presc_quimio ( nr_prescricao_p bigint, nr_seq_prescricao_p bigint) RETURNS timestamp AS $body$
DECLARE

dt_retorno_w	timestamp;
nr_seq_atendimento_w	bigint;
nr_seq_material_w		bigint;

BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_prescricao_p IS NOT NULL AND nr_seq_prescricao_p::text <> '') then

	select	max(nr_seq_atendimento),
		max(nr_seq_material)
	into STRICT	nr_seq_atendimento_w,
		nr_seq_material_w
	from	prescr_material
	where	nr_prescricao	= nr_prescricao_p
	and	nr_sequencia	= nr_seq_prescricao_p;

	if (nr_seq_material_w IS NOT NULL AND nr_seq_material_w::text <> '') and (nr_seq_atendimento_w IS NOT NULL AND nr_seq_atendimento_w::text <> '') then
		select	max(dt_administracao)
		into STRICT	dt_retorno_w
		from	paciente_atend_medic_adm
		where	nr_seq_atendimento	= nr_seq_atendimento_w
		and	nr_seq_material		= nr_seq_material_w;
	end if;

end if;

return	dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
