CREATE OR REPLACE FUNCTION obter_data_agenda_cons_oftal ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


dt_agenda_w	varchar(100);


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	select	to_char(MAX(dt_agenda),'hh24:mi')
	into STRICT	dt_agenda_w
	from	agenda_consulta
	where	nr_seq_oftalmo = nr_sequencia_p;
	if (coalesce(dt_agenda_w::text, '') = '') then
		select	to_char(MAX(hr_inicio),'hh24:mi')
		into STRICT	dt_agenda_w
		from	agenda_paciente
		where	nr_seq_oftalmo = nr_sequencia_p;
	end if;
end if;

return	dt_agenda_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
