CREATE OR REPLACE FUNCTION obter_data_agenda ( nr_atendimento_p bigint, ie_opcao_p text) RETURNS timestamp AS $body$
DECLARE


dt_agenda_w	timestamp;

/*
ie_opcao_p
 C - Agenda Consulta
 P - Agenda Paciente
*/
BEGIN

if (ie_opcao_p	= 'C') then

	select	max(dt_agenda)
	into STRICT	dt_agenda_w
	from 	agenda_consulta
	where 	nr_atendimento	= nr_atendimento_p;


elsif (ie_opcao_p	= 'P') then

	select	max(dt_agenda)
	into STRICT	dt_agenda_w
	from 	agenda_paciente
	where 	nr_atendimento	= nr_atendimento_p;

end if;

RETURN	dt_agenda_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
